import React, {useState} from 'react'
import { fetchMoviesData, fetchMoviesImages } from '../../Services/Utils/ApiRequests';
import './NowPlaying.scss'

function NowPlaying() {
const [movieData, setMovieData] = useState(null);
const [movieImageData, setMovieImageData] = useState([]);

const fetchMovies = async () => {
  try {
    const data = await fetchMoviesData();
    setMovieData(data); 
    console.log(data);

    const movieId = data.results.map((movie) => {
      return `https://image.tmdb.org/t/p/w500${movie.poster_path}`;
    });

    // Faz o fetch das imagens com base nas URLs
    const imageResponses = await Promise.all(
      imageUrls.map((imageUrl) => fetchMoviesImages(imageUrl))
    );

    // Converte as respostas em dados de imagem
    /* const imageData = await Promise.all(
      imageResponses.map(async (response) => {
        const blob = await response.blob();
        return URL.createObjectURL(blob);
      })
    ); */

    setMovieImageData(imageData); // Define os dados da imagem no estado
    } catch (error) {
      console.error(error);
    }
};

/* const fetchMovieImages = async () => {
  try {
    const data = await fetchMoviesImages(poster_path);
    setMovieImageData(data); // Define os dados da API no estado
    console.log(data);
  } catch (error) {
    console.error(error);
  }
}; */

  return (
    <div>
      <button onClick={fetchMovies}>Clique para buscar filmes</button>
      {/* <button onClick={fetchMovieImages}>Clique para buscar imagens dos filmes</button> */}
      {movieData && (
        <div>
          <h2>Filmes em cartaz</h2>
          <ul className='playing-movies' style={{listStyleType: "none"}}>
            {movieData.results.map((movie, index) => (
              <li key={movie.id}>
                {movie.title}
                {movieImageData[index] && (
                  <img src={`https://api.themoviedb.org/3/movie/${movie.id}/images/${movie.poster_path}`} alt={movie.title} />
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

export default NowPlaying
