import React, { useState, useEffect } from 'react';
import './NowPlaying.scss';
import cinemaStatic from '../../Assets/Images/cinema_icon_static.png';
import cinemaHover from '../../Assets/Images/output-onlinegiftools.gif';

function NowPlaying({ movieData, currentMovieIndex, currentMovieGenre, movieTimes }) {
  const [hover, setHover] = useState(false);
  const displayedMovies = movieData && movieData.slice(0, 6);
  const adultGenres = ["Horror", "Thriller", "Drama"];

  const isAdultMovie = (genres) => {
    // Divide a string de gêneros em uma matriz de gêneros
    const genreArray = genres.split(',').map((genre) => genre.trim());
    // Verifica se pelo menos um gênero na lista está na lista adultGenres
    return genreArray.some((genre) => adultGenres.includes(genre));
  };

  return (
    <div>
      {displayedMovies && (
        <div id='playing-movies'>
          <span>
            <h2>Filmes em cartaz</h2>
            <div className='check-all-movies'>
              <img src={hover ? cinemaHover : cinemaStatic} alt="Cinema" />
              <a
                onMouseEnter={() => setHover(true)}
                onMouseLeave={() => setHover(false)}
              >Ver todos os filmes</a>
            </div>
          </span>
          <ul>
            {displayedMovies.map((movie, index) => (
              <li key={movie.id}>
                <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
                <div className='movie-poster-info'>
                  <h3>{movie.title}</h3>
                  <p>{currentMovieGenre}</p>
                  <p>{movie.adult == true && adultGenres.includes(currentMovieGenre.split(',')) ? "M18 • " : "M14 • " + movieTimes[index]}</p>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default NowPlaying;
