          <h2>Filmes em cartaz</h2>
          <ul className='playing-movies' style={{listStyleType: "none"}}>
            {movieData.map((movie, index) => (
              <li key={movie.id}>
                {movie.title}
                <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

export default NowPlaying
