import React, { useState, useEffect } from 'react';
import './App.css';
import Header from "./Layout/Header/Header";
import MainIntro from './Layout/MainIntro/MainIntro';
import NowPlaying from './Layout/NowPlaying/NowPlaying';
import { fetchMoviesData, fetchMoviesGenres } from './Services/Utils/ApiRequests';

function App() {
  const [isMobile, setIsMobile] = useState(false);
  const [movieData, setMovieData] = useState(null);
  const [movieGenres, setMovieGenres] = useState(null);
  const [currentMovieGenre, setCurrentMovieGenre] = useState([]);
  const [currentMovieIndex, setCurrentMovieIndex] = useState(0); // Índice inicial
  const intervalTime = 10000; // Alterar a cada 5 segundos (ou o que desejar)

  const fetchMovies = async () => {
    try {
      const data = await fetchMoviesData();
      const sortedMovies = data.results
        .sort((a, b) => b.vote_average - a.vote_average) // Classificar em ordem decrescente de média de votos
        .slice(0, 6); // Pegar os 6 primeiros filmes da lista classificada
      setMovieData(sortedMovies);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchMovieGenres = async () => {
    try {
      const data = await fetchMoviesGenres();
      const genres = data.genres || [];
      setMovieGenres(genres);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    if (movieData && movieData[currentMovieIndex]) {
      const currentMovieGenreIds = movieData[currentMovieIndex].genre_ids || [];
      const currentMovieGenreNames = currentMovieGenreIds.map((genreId) => {
        const genre = movieGenres?.find((genre) => genre.id === genreId);
        return genre ? genre.name : "";
      }).join(", ");
      setCurrentMovieGenre(currentMovieGenreNames);
    }
  }, [currentMovieIndex, movieData, movieGenres]);

  useEffect(() => {
    fetchMovies();
    fetchMovieGenres();

    // Configurar um intervalo para alterar o filme exibido a cada x segundos
    const intervalId = setInterval(() => {
      setCurrentMovieIndex((prevIndex) => (prevIndex + 1) % 6); // Alternar entre os 6 filmes
    }, intervalTime);

    const newMovieData = {...movieData, ...currentMovieGenres}

    return () => {
      // Limpar o intervalo quando o componente é desmontado
      clearInterval(intervalId);
    };
  }, [])

  return (
    <div className="App">
      <Header />
      {movieData && currentMovieGenre && (
        <MainIntro mainMovie={movieData ? movieData : null} currentMovieIndex={currentMovieIndex} currentMovieGenre={currentMovieGenre} />
      )}
      <NowPlaying currentMovieGenre={movieGenres} currentMovieIndex={currentMovieIndex} movieData={movieData} />
    </div>
  );
}

export default App;
