import React, {useState, useEffect} from 'react'
import { fetchMoviesData, fetchMoviesImages } from '../../Services/Utils/ApiRequests';
import './NowPlaying.scss'
import cinemaStatic from '../../Assets/Images/cinema_icon_static.png';
import cinemaHover from '../../Assets/Images/cinema_icon.gif';

function NowPlaying() {
  const [movieData, setMovieData] = useState(null);
  const fetchMovies = async () => {
    try {
      const data = await fetchMoviesData();
      const sortedMovies = data.results
        .sort((a, b) => b.vote_average - a.vote_average) // Classificar em ordem decrescente de média de votos
        .slice(0, 6); // Pegar os 6 primeiros filmes da lista classificada
      setMovieData(sortedMovies);
      console.log(sortedMovies);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [])


  return (
    <div>
      {movieData && (
        <div id='playing-movies'>
          <span>
            <h2>Filmes em cartaz</h2>
            <div>
              <img src={cinemaHover}></img>
              <a>Ver todos os filmes</a>
            </div>
          </span>
          <ul>
            {movieData.map((movie, index) => (
              <li key={movie.id}>
                <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  )
}

export default NowPlaying
