import { fetchMoviesData, fetchMoviesImages } from '../../Services/Utils/ApiRequests';
import './NowPlaying.scss'
import cinemaStatic from '../../Assets/Images/cinema_icon_static.png';
import cinemaHover from '../../Assets/Images/output-onlinegiftools.gif';

function NowPlaying({ movieData, currentMovieIndex, currentMovieGenre }) {
