              <a
                onMouseEnter={() => setHover(true)}
                onMouseLeave={() => setHover(false)}
              >Ver todos os filmes</a>
            </div>
          </span>
          <ul>
            {movieData.map((movie, index) => (
              <li key={movie.id}>
                <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
