import React, { useState, useEffect } from 'react';
import './NowPlaying.scss';
import cinemaStatic from '../../Assets/Images/cinema_icon_static.png';
import cinemaHover from '../../Assets/Images/output-onlinegiftools.gif';

function NowPlaying({ movieData, currentMovieIndex, currentMovieGenre }) {
  const [hover, setHover] = useState(false);
  const [movieTimes, setMovieTimes] = useState([]); // Estado para armazenar os valores de movieTime

  // Use useEffect para gerar os valores de movieTime apenas na primeira renderização
  useEffect(() => {
    const generatedMovieTimes = movieData.map(() => {
      return `${Math.floor(Math.random() * (180 - 60 + 1)) + 60} Min`;
    });
    setMovieTimes(generatedMovieTimes);
  }, [movieData]); // Executa apenas quando movieData é alterado

  return (
    <div>
      {movieData && (
        <div id='playing-movies'>
          <span>
            <h2>Filmes em cartaz</h2>
            <div className='check-all-movies'>
              <img src={hover ? cinemaHover : cinemaStatic} alt="Cinema" />
              <a
                onMouseEnter={() => setHover(true)}
                onMouseLeave={() => setHover(false)}
              >Ver todos os filmes</a>
            </div>
          </span>
          <ul>
            {movieData.map((movie, index) => (
              <li key={movie.id}>
                <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
                <div className='movie-poster-info'>
                  <h3>{movie.title}</h3>
                  <p>{currentMovieGenre[index]?.name}</p>
                  <p>{movie.adult ? "M18 • " : "M14 • " + movieTimes[index]}</p>
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default NowPlaying;
