import React, { useState } from 'react';
import './NowPlaying.scss';
import cinemaStatic from '../../Assets/Images/cinema_icon_static.png';
import cinemaHover from '../../Assets/Images/output-onlinegiftools.gif';

function NowPlaying({ movieData, currentMovieIndex, movieTimes, movieGenres }) {
  const [hover, setHover] = useState(false);
  const displayedMovies = movieData && movieData.slice(0, 6);
  const adultGenres = ["Horror", "Thriller", "Drama"];

  const getMovieGenres = (movieIndex) => {
    if (movieGenres && movieData && movieData[movieIndex]) {
      const movieGenreIds = movieData[movieIndex].genre_ids || [];
      const movieGenreNames = movieGenreIds.map((genreId) => {
        const genre = movieGenres.find((genre) => genre.id === genreId);
        return genre ? genre.name : "";
      });
      return movieGenreNames.join(", ");
      isAdultMovie(genre.name);
    }
    return "";
  };

  const isAdultMovie = (currentGenre) => {
    console.log("Inside isAdultMovie function with movieIndex:", currentMovieGenre);

    const genres = movieGenres.split(',').map((genre) => genre.trim());
    console.log("Genre Array:", genres);

    return genres.some((genre) => adultGenres.includes(genre));
  };

  return (
    <div>
      {displayedMovies && (
        <div id='playing-movies'>
          <span>
            <h2>Filmes em cartaz</h2>
            <div className='check-all-movies'>
              <img src={hover ? cinemaHover : cinemaStatic} alt="Cinema" />
              <a
                onMouseEnter={() => setHover(true)}
                onMouseLeave={() => setHover(false)}
              >Ver todos os filmes</a>
            </div>
          </span>
          <ul>
            {displayedMovies.map((movie, index) => (
              <li key={movie.id}>
                <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
                <div className='movie-poster-info'>
                  <h3>{movie.title}</h3>
                  <p>{getMovieGenres(index)}</p>

                  {<p>{movie.adult === true && isAdultMovie ? "M18 • " : "M14 • " + movieTimes[index]}</p>}
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default NowPlaying;
