import React, { useState } from 'react'
import './MainIntro.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlay } from '@fortawesome/free-solid-svg-icons'

function MainIntro({ mainMovie, currentMovieIndex }) {
  const [backgroundImage, setBackgroundImage] = useState(
    movieData && movieData[currentMovieIndex]
      ? `url(https://image.tmdb.org/t/p/w500${movieData[currentMovieIndex].poster_path})`
      : ''
  );
  useEffect(() => {
    // Configurar um temporizador para alterar o plano de fundo a cada x segundos
    const timerId = setTimeout(() => {
      const nextIndex = (currentMovieIndex + 1) % 6; // Alternar entre os 6 filmes
      setBackgroundImage(
        movieData && movieData[nextIndex]
          ? `url(https://image.tmdb.org/t/p/w500${mainMovie[nextIndex].poster_path})`
          : ''
      );
    }, 5000); // Alterar a cada 5 segundos (ou o que desejar)

    return () => {
      // Limpar o temporizador quando o componente é desmontado ou quando o índice muda
      clearTimeout(timerId);
    };
  }, [currentMovieIndex, movieData]);

  return (
    <div>
      <div className='main-intro'>
        <div className='video-player' style={{
            backgroundImage: backgroundImage,
            backgroundSize: 'cover',
          }}>
          <FontAwesomeIcon className='icon' icon={faPlay} />
        </div>
        <div className='movie-info'>
          asdasda
        </div>
      </div>
    </div>
  )
}

export default MainIntro
